mapper.insert:
insert into t_ecg_device (id, ecg_device_run_mode, ecg_channel_type
  )
values (#{id,jdbcType=INTEGER}, #{ecgDeviceRunMode,jdbcType=VARCHAR}, #{ecgChannelType,jdbcType=VARCHAR}
  )

mapper.update:
update t_ecg_device
set ecg_device_run_mode = #{ecgDeviceRunMode,jdbcType=VARCHAR},
  ecg_channel_type = #{ecgChannelType,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, ecg_device_run_mode, ecg_channel_type
from t_ecg_device
where id = #{id,jdbcType=INTEGER}
